import React, {useEffect, useState} from 'react';
import SmallCard from './SmallCard';
// import todosLosProductos from '../funciones/funciones'

function ContentRowInfo(){
    const [products, setProducts] = useState();
    const [users, setUsers] = useState();
    const [categories, setCategories] = useState()

/*  Cada set de datos es un objeto literal */

/* <!-- Products in DB --> */

const data =[

{
    title: 'Total de productos',
    datosBD: products,
    color: 'primary', 
    icon: 'fa-boxes'
},

/* <!-- Total users --> */

{
    title:'Total de usuarios', 
    datosBD: users,
    color:'success', 
    icon:'fa-user-check'
},

/* <!-- Total Categories --> */

 {
    title:'Total de categorías',
    datosBD: categories,
    color:'warning',
    icon:'fa-award'
}

]

useEffect(() => {

    fetch("http://localhost:3001/api/products/")
    .then(resultado => resultado.json())
    .then(dataProduct => {
        console.log(dataProduct)
        setProducts(dataProduct.meta.count)
    })

    fetch("http://localhost:3001/api/users")
    .then(resultado => resultado.json())
    .then(dataUser => {
        console.log(dataUser)
        setUsers(dataUser.meta.count)
    })
    fetch("http://localhost:3001/api/products/categories")
    .then(resultado => resultado.json())
    .then(dataCategories => {
        console.log(dataCategories)
        setCategories(dataCategories.meta.categoryCount)
    })

}, [])



return (
    <>
        <div className="row">
            <div className="col-">
            {data.map((item, i) => 
                <div key={i} 
                    title={item.title} 
                    datosBD={item.datosBD}
                    color={item.color}
                    icon={item.icon}
                /> 
            )}
            </div>

        </div>
    </>    
)
}

export default ContentRowInfo;
