import React from 'react';
import PropTypes from 'prop-types';

function SmallCard(props){
    return(
        <div className="col-md-4 mb-4">
            <div className={`card border-left-${color} shadow h-100 py-2`}>
                <div className="card-body">
                    <div className="row no-gutters align-items-center">
                        <div className="col mr-2">
                            <div className={`text-xs font-weight-bold text-${color} text-uppercase mb-1`}> {title}</div>
                            <div className="h5 mb-0 font-weight-bold text-gray-800">{datosBD}</div>
                        </div>
                        <div className="col-auto">
                            <i className={`fas ${props.icon} fa-2x text-gray-300`}></i>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    )
}


/* PROPTYPES */

SmallCard.propTypes = {

    title: PropTypes.string.isRequired,
    color: PropTypes.oneOf(["primary","success","warning"]).isRequired,
    datosBD: PropTypes.oneOfType([
        PropTypes.string,
        PropTypes.number
    ]).isRequired,
    icon: PropTypes.string

}

/* DEFINICIÓN DE PROPIEDADES POR DEFAULT */

SmallCard.defaultProps = {
    title: "No tiene texto!",
    color: "primary",
    datosBD: "0",
    icon: null
}


export default SmallCard;
