import React, {useEffect, useState} from 'react';
import SmallCard from './SmallCard';
// import todosLosProductos from '../funciones/funciones'

function ContentRowInfo(){
    let [products, setProducts] = useState("No dispone");
    let [users, setUsers] = useState("Sin usuarios");
    let [orders, setOrders] = useState("No dispone")

/*  Cada set de datos es un objeto literal */

/* <!-- Products in DB --> */

let productsInDB = {
    title: 'Total de productos',
    color: 'primary', 
    icon: 'fa-boxes'
}

/* <!-- Total users --> */

let totalUsers = {
    title:' Total de usuarios', 
    color:'success', 
    icon:'fa-user-check'
}

/* <!-- Total Categories --> */

let totalCategories = {
    title:'Total de categorías' ,
    color:'warning',
    icon:'fa-award'
}

useEffect(() => {
    fetch("http://localhost:3001/api/products/total")
    .then(resultado => resultado.json())
    .then(dataProduct => {
        setProducts(dataProduct.meta.total_products)
    })

    fetch("http://localhost:3001/api/products/categories")
    .then(resultado => resultado.json())
    .then(dataOrders => {
        setOrders(dataOrders.meta.total_orders)
    })

    fetch("http://localhost:3001/api/users")
    .then(resultado => resultado.json())
    .then(dataUser => {
        setUsers(dataUser.meta.total_users)
    })
}, [])



return (
    <>
        <div className="row">
            {datos.map((dato, i) => 
                <SmallCard key={i} 
                    titulo={dato.titulo} 
                    cifra={dato.cifra}
                    colorBorder={dato.colorBorder}
                    icono={dato.icono}
                /> 
            )}
        </div>
    </>    
)
}

export default ContentRowInfo;
