import React, {useEffect, useState} from 'react';
import SmallCard from './SmallCard';
import axios from 'axios'
// import todosLosProductos from '../funciones/funciones'

function ContentRowInfo(){
    const [products, setProducts] = useState();
    const [users, setUsers] = useState();
    const [categories, setCategories] = useState()

/*  Cada set de datos es un objeto literal */

/* <!-- Products in DB --> */

const data =[

{
    title: 'Total de productos',
    datosBD: products,
    color: 'primary', 
    icon: 'fa-boxes'
},

/* <!-- Total users --> */

{
    title:'Total de usuarios', 
    datosBD: users,
    color:'success', 
    icon:'fa-user-check'
},

/* <!-- Total Categories --> */

 {
    title:'Total de categorías',
    datosBD: categories,
    color:'warning',
    icon:'fa-award'
}
]

useEffect(() => {

    const todosLosProductos = async () => {
    const peticion = await axios.get('http://localhost:3001/api/products')
    setProducts(peticion.data.products)
    }


    // fetch("localhost:3001/api/products/")
    // .then(resultado => resultado.json())
    // .then(dataProduct => {
    //     console.log(dataProduct)
    //     setProducts(dataProduct.meta.total_products)
    // })

    fetch("http://localhost:3001/api/users")
    .then(resultado => resultado.json())
    .then(dataUser => {
        console.log(dataUser)
        setUsers(dataUser.meta.total_users)
    })
    fetch("http://localhost:3001/api/products/categories")
    .then(resultado => resultado.json())
    .then(dataCategories => {
        console.log(dataCategories)
        setCategories(dataCategories.meta.categoryCount)
    })

}, [])



return (
    <>
        <div className="row">
            {data.map((item, i) => 
                <SmallCard key={i} 
                    title={item.title} 
                    datosBD={item.datosBD}
                    color={item.color}
                    icon={item.icon}
                /> 
            )}
        </div>
    </>    
)
}

export default ContentRowInfo;
